var assert = require('assert')
var angular = require('exports?window.angular!./angular.js')
var testModule = angular.module('test', [])

describe('ng-annotate', function() {
  it('should minify angular without errors', function() {
    assert(typeof angular.version, 'object')
  })

  it('should annotate this thing', function(done) {
    testModule.controller('testCtrl', function($http) {
      console.log(assert(typeof $http.get, 'function'))
      done()
    })
  })
})
